import React from 'react';
import '../style/timeLine.css'

export default class TimeLineItem extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            ifChoosed: false,
        }
    }

    handleClick = (e) => {
        this.setState({
            ifChoosed: true,
        })
    }

    render() {
        const { firstTitle, secondTitle} = this.props;
        const { ifChoosed } = this.state;
        return (
            <div className='itemDiv' onClick= {this.handleClick}>
                <div className={ifChoosed ? 'choosedFist':'firstDiv'}>{firstTitle}</div>
                <div className={ifChoosed ? 'choosedSecond':'secondDiv'}>{secondTitle}</div>
                {ifChoosed ? <div className='redLine'></div> : null}
            </div>
        )
    }
}
