import React from 'react';
import '../style/cityLine.css';
import 'antd/dist/antd.css';
import CityLineItem from './CityLineItem.js';
import MuseumList from './MuseumList';

export default class CityLineCom extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            data : [
                {
                    cityName: '北京',
                    ifChoosed: true,
                    id: 0,
                },
                {
                    cityName: '陕西',
                    ifChoosed: false,
                    id: 1,
                },
                {
                    cityName: '河南',
                    ifChoosed: false,
                    id: 2,
                },
                {
                    cityName: '四川',
                    ifChoosed: false,
                    id: 3,
                },
                {
                    cityName: '重庆',
                    ifChoosed: false,
                    id: 4,
                },
                {
                    cityName: '湖南',
                    ifChoosed: false,
                    id: 5,
                },
                {
                    cityName: '湖北',
                    ifChoosed: false,
                    id: 6,
                },
                {
                    cityName: '安徽',
                    ifChoosed: false,
                    id: 7,
                },
            ]
        }
    }
    handleClick = (clickId)  => {
            let tempdata = this.state.data;
            tempdata.map((item,index) => {
                    if(item.id == clickId){
                        item.ifChoosed = true;
                    }
                    else{
                        item.ifChoosed = false;
                    }
                }
            )
            this.setState({
                    data: tempdata,
                }
            )
    }
    componentDidMount(){
        let domHeight = document.getElementsByClassName('cityLineDiv')[0].offsetHeight;
        document.getElementsByClassName('grayLine2')[0].style.height= `${domHeight}px`;
      }
    render(){
        const { data } =this.state;
        return(
            <div>
                <div className = 'cityLineDiv'>{
                    data.map((item, index) => {
                                return(
                                    <CityLineItem
                                        key={index}
                                        cityName={item.cityName}
                                        ifChoosed={item.ifChoosed}
                                        onCityClicked={this.handleClick.bind(this,item.id)}
                                    ></CityLineItem>
                                )
                            }
                        )
                    }
                    <div className = 'grayLine2'></div>
                </div>
                <div className='contentDiv'>
                    <MuseumList ifMuseumList={selectedId == 0 ? true : false} content={museumlist1}>
                        {selectedId == 0 ? <CommentCom /> : null}
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 1 ? true : false} content={museumlist2}>
                        {selectedId == 1 ? <CommentCom /> : null}
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 2 ? true : false} content={museumlist3}>
                        {selectedId == 2 ? <CommentCom /> : null} 
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 3 ? true : false} content={museumlist4}>
                        {selectedId == 3 ? <CommentCom /> : null}
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 4 ? true : false} content={museumlist5}>
                        {selectedId == 4 ? <CommentCom /> : null}
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 5 ? true : false} content={museumlist6}>
                        {selectedId == 5 ? <CommentCom /> : null} 
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 6 ? true : false} content={museumlist7}>
                        {selectedId == 6 ? <CommentCom /> : null}
                    </MuseumList>
                    <MuseumList ifMuseumList={selectedId == 7 ? true : false} content={museumlist8}>
                        {selectedId == 7 ? <CommentCom /> : null} 
                    </MuseumList>
                </div>
            </div>
        )
    }
}
