import React from 'react';
import './App.css';
import HeadTitle from './component/HeadTitle.js'
import shou from './assets/shou.png'
import gui from './assets/gui.png'
import guan from './assets/guan.png'
import TimeLineCom from './component/TimeLineCom.js'

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      clickedIndex: 0,
    }
  }
  handleClick = (index) => {
    this.setState({
      clickedIndex: index,
    })
  }

  render () {
    const { clickedIndex } = this.state;
    return (
      <div>
        <div style={{display: 'inline-block',marginLeft: '140px',marginTop: '160px',}}>
          <HeadTitle thirdPartFont='游记讯息' onHeadClick={this.handleClick.bind(this, 0)} ifClicked={clickedIndex == 0 ? true : false}>
            <img className='headImg' src={shou}></img>
          </HeadTitle>
          <HeadTitle thirdPartFont='博物馆志' onHeadClick={this.handleClick.bind(this, 1)} ifClicked={clickedIndex == 1 ? true : false}>
            <img className='headImg' src={guan}></img>
          </HeadTitle>
          <HeadTitle thirdPartFont='行程规划' onHeadClick={this.handleClick.bind(this, 2)} ifClicked={clickedIndex == 2 ? true : false}>
            <img className='headImg' src={gui}></img>
          </HeadTitle>
        </div>
        <div>
          <TimeLineCom />
        </div>
      </div>
    )
  }
}

export default App;
