import React from 'react';
import '../style/comment.css';
import { Comment, List, Input,Button } from 'antd';
import 'antd/dist/antd.css';
import moment from 'moment';

const { TextArea } = Input;


export default class CommentCom extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            commentData: [
                            {
                            author: '123497459@qq.com',
                            content: (
                                <p>
                                    写的挺好的!
                                </p>
                            ),
                            datetime: (
                                <span>
                                    {moment()
                                    .subtract(1, '天')
                                    .fromNow()}
                                </span>
                            ),
                            },
                            {
                            author: 'sdzqlyn@gmail.com',
                            content: (
                                <p>
                                666
                                </p>
                            ),
                            datetime: (
                                <span>
                                    {moment()
                                    .subtract(2, '天')
                                    .fromNow()}
                                </span>
                            ),
                            },
                        ],
            value: 1111111111,
            value2: null,
        }
    }
    onChange(){
    }

    onSubmit(){
    }

    onEmail(){

    }

    render(){
        const { commentData, onChange, onSubmit, value, onEmail,value2 } = this.state;
        return(
            <div className = 'commentDiv'>
                 <List
                    className="comment-list"
                    header={`${commentData.length} 评论`}
                    itemLayout="horizontal"
                    dataSource={commentData}
                    renderItem={item => (
                    <li>
                        <Comment
                        actions={item.actions}
                        author={item.author}
                        avatar={item.avatar}
                        content={item.content}
                        datetime={item.datetime}
                        />
                    </li>
                    )}
                />
                <TextArea rows={4} onChange={onChange} value={value} />
                <Button htmlType="submit"  onClick={onSubmit} type="primary">提交评论</Button>
                <Input onChange={onEmail} value={value2} />
            </div>
        )
    }
}
