    render(){
        const { commentData, onChange, onSubmit, value, onEmail, value2} = this.state;
        return(
            <div className = 'commentDiv'>
                 <List
                    className="comment-list"
                    header={<div className = 'commentNum'> {commentData.length} 评论</div>}
                    itemLayout="horizontal"
                    dataSource={commentData}
                    renderItem={item => (
                    <li>
                        <Comment
                        actions={item.actions}
                        author={item.author}
                        avatar={item.avatar}
                        content={item.content}
                        datetime={item.datetime}
                        />
                    </li>
                    )}
                />
                <TextArea className = 'textArea1' rows={4} onChange={onChange} value={value} />
